    private void SetUpGame()
    {
        animalIconShow = true;
        // Reload new animal list
        Random random = new Random();
        shuffledAnimals = animalEmoji
            .OrderBy(item => random.Next())
            .ToList();

        // Reset the matches
        matchesFound = 0;

        // Reset the mistakes
        mistakes = 0;

        // Reset the timer
        tenthsOfSecondsElapsed = 0;

